require 'net/smtp'
def send_email(to, subject = "", body = "") from = "my@email.com" body= "From: #{from}
To: #{to}
Subject: #{subject}

#{body}
"
Net::SMTP.start('192.168.10.213', 25, '192.168.0.218') do |smtp| smtp.send_message body, from, to end end
send_email "my@email.com", "test", "blah blah blah"
